import { FunctionComponent, useState } from "react";

export type Operation = "decrease" | "increase";

interface Props {
  removeProductCallback: (productId: number) => void;
  handleUpdateQuantity: (productId: number, operation: Operation) => void;
  productId: number;
}

export const Quantifier = ({
    removeProductCallback,
    handleUpdateQuantity,
    productId,
}: Props) => {
  const [value, setValue] = useState<number>(1);

  const reduce = (): void => {
    handleUpdateQuantity(productId, "decrease");

    setValue((prevState: number) => {
      const updatedValue = prevState - 1;
      if (updatedValue === 0) {
        removeProductCallback(productId);
      }
      return updatedValue;
    });
  };

  const increase = (): void => {
    handleUpdateQuantity(productId, "increase");
    setValue((prevState: number) => prevState + 1);
  };

  return (
    <div className="quantifier">
      <input type="button" value="-" className="buttonMinus" onClick={reduce} />
      <input
        type="number"
        step="1"
        max=""
        value={value}
        onChange={(e) => setValue(parseInt(e.target.value))}
        className="quantityField"
      />
      <input
        type="button"
        value="+"
        className="buttonPlus"
        onClick={increase}
      />
    </div>
  );
};
