import { Row, Col, Container } from "react-bootstrap";
import "../styling/footer.css";

const location = window.location.href;

export const Footer = () => {
  return (
    <div className="footer">
      <Container>
        {location == "/" ? (
          <Row className="social">
            <h2>FOLLOW OUR STORY</h2>
            <h3>@thenujastore</h3>
          </Row>
        ) : null}
        <Row className="copy">
          <p>thenujastore &copy; 2021</p>
        </Row>
      </Container>
    </div>
  );
};
