import rosebud from "../assets/categories/rosebud.jpg";
import fairytale from "../assets/categories/necklace-thumbnail.jpg";
import pastel from "../assets/categories/pastel-dreams.jpg";
import "../styling/showCategory.css";
import { Row, Col, Container } from "react-bootstrap";
import { useLocation } from "react-router-dom";
import { NavBar } from "./navbar";
import { Footer } from "./footer";

export const ShowCategory = () => {
  const location = useLocation().pathname;
  console.log("location", location);
  var title: string = "";
  if (location === "/necklace") {
    title = "NECKLACES";
  } else if (location === "/bracelet") {
    title = "BRACELETS";
  } else if (location === "/earrings") {
    title = "EARRINGS";
  } else if (location === "/rings") {
    title = "RINGS";
  }

  const categoryData = productData.filter(
    (product) => product.category === location
  );

  console.log(categoryData);

  return (
    <div className="product-container">
      <NavBar />
      <h2>{title}</h2>
      <Container fluid>
        <Row>
          {categoryData.map((item) => {
            return (
              <Col xs={6} md={4} xl={3}>
                <div className="category-item">
                  <img src={item.image} alt="" className="img-fluid" />
                  <h3>{item.name}</h3>
                  <h4>{item.price}</h4>
                </div>
              </Col>
            );
          })}
          ;
        </Row>
      </Container>
      <Footer />
    </div>
  );
};

